import React from 'react';
import Styled from 'styled-components';

const ResultCardWrap = Styled.div`
img {
    width: 200px;
    height: 200px;
    border-radius: 10px;
    -webkit-box-shadow: 0px 7px 14px 7px rgba(255,255,255,0.35); 
    box-shadow: 0px 7px 14px 7px rgba(255,255,255,0.35);
}

div {
    font-size: 23px
}

.result_card{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 400px;
    height: 450px;
    background-color: white;
}

.result_list{
    display: flex;
}

.result_list > div{
    text-align: center;
    width: 100px;
    margin: 10px 15px;
}

div > p{
    text-align: center;
}
`;

const ResultCard = ({ data }) => {
    return data && (
        <ResultCardWrap>
            <div className='result_card'>
                <img src={data.avatar_url} alt="" />
                <p className='result_id'>{data.login}</p>
                <div className='result_list'>
                    <div className='result_followers'>Followers
                    <p>{data.followers}</p></div>
                    <div className='result_following'>Following
                    <p>{data.following}</p></div>
                    <div className='result_repos'>Repos
                    <p> {data.public_repos} </p></div>
                </div>
            </div>
        </ResultCardWrap>
    );
}

export default ResultCard;
